

tinymce.PluginManager.add('browseruploader', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('browseruploader', {
        text: 'Obrazki',
        icon: false,
        onclick: function() {
            // Open window
            editor.windowManager.open({
                title: 'Obrazki',
                body: [
                    {type: 'textbox', name: 'title', label: 'Źródło'}
                ],
                onsubmit: function(e) {
                    // Insert content when the window form is submitted
                    editor.insertContent('<img src="' + e.data.title + '" width="500" />');
                }
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('browseruploader', {
        text: 'Obrazki',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Upload obrazków',
                url: '/uploader/index',
                width: 850,
                height: 450,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });
});